package com.example.design_application;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.TextView;import androidx.annotation.NonNull;import androidx.recyclerview.widget.RecyclerView;import java.util.List;class AdapterDirectories extends RecyclerView.Adapter<AdapterDirectories.OnViewHolder> {    public static class OnViewHolder extends RecyclerView.ViewHolder {        TextView nameD;        ImageView imageD;        public OnViewHolder( View itemView) {            super(itemView);            nameD =(TextView) itemView.findViewById(R.id.name_directory);            imageD =(ImageView) itemView.findViewById(R.id.image_directory);        }    }    List<String> listitem;    public AdapterDirectories(List<String> listitem) {        this.listitem = listitem;    }    @NonNull    @Override    public OnViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        View view = LayoutInflater.from(parent.getContext()).inflate(R.layout.directories_layout,parent,false);        return new OnViewHolder(view);    }    @Override    public void onBindViewHolder(@NonNull OnViewHolder holder, int position) {        holder.nameD.setText(listitem.get(position));    }    @Override    public int getItemCount() {        return listitem.size();    }}