package com.example.design_application;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.TextView;import androidx.annotation.NonNull;import androidx.recyclerview.widget.RecyclerView;import java.util.List;class AdapterFiles extends RecyclerView.Adapter<AdapterFiles.myViewHolder> {    List<String> listitem2;    public AdapterFiles(List<String> listitem2) {    }    @NonNull    @Override    public myViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        View v = LayoutInflater.from(parent.getContext()).inflate(R.layout.files_layout,parent,false);        return new myViewHolder(v);    }    @Override    public void onBindViewHolder(@NonNull myViewHolder holder, int position) {        holder.name_file.setText(listitem2.get(position));    }    @Override    public int getItemCount() {        return listitem2.size();    }    public class myViewHolder extends RecyclerView.ViewHolder{        TextView name_file , path_file ;         ImageView file_image;         public myViewHolder(@NonNull View itemView) {            super(itemView);            name_file = itemView.findViewById(R.id.nameoffile);            path_file = itemView.findViewById(R.id.text_path);            file_image = itemView.findViewById(R.id.image_file);        }    }}